import React, { useEffect, useState } from "react";
import "./App.css";

const Chat = ({ socket, room, userName }) => {
  const [currentMessage, setCurrentMessage] = useState("");
  const [messageList, setMessageList] = useState([]);
  const [userCount, setUserCount] = useState(0);
  const [activeUsers, setActiveUsers] = useState([]);

  const sendMessage = async () => {
    if (currentMessage !== "") {
      const messageData = {
                room: room,
                author: userName,
                message: currentMessage,
                time: new Date().toLocaleString()
            }

            await socket.emit('send_message', messageData)
            setMessageList((list) => [...list, messageData])
            setCurrentMessage('')
        }    
    };

    useEffect(() => {
        socket.emit('join_room', { room: room,userName: userName });
        socket.on('receive_message', (data) => {
        setMessageList((list) => [...list, data]) 
        });

        socket.on('update_user_count', (data) => {
        setUserCount(data.userCount);
        });

        socket.on('active_users', (data) => {
        setActiveUsers(data);
        });

    }, [socket, room, userName]);

    return (
        
        <>
        <div>
            <p className='user-count'>{userCount /2} Active Users</p>
            <p className='room-name'>Hi {userName}, You are in room: {room}</p>
            <p className="active-users">Active Users: {activeUsers.map(user => <span key={user}>{user}, </span>)}</p>
        </div>
        <div className='chat-container'>
            {messageList.map((messageContent, index) => 
                <div className='message' key={index}>
                    <p className='author'>{messageContent.author} </p>
                    <p className='message-text'>{messageContent.message}</p>
                    <p className='time'>{messageContent.time}</p>
                </div>  
            )}
           
            <div className='message-input'>
                <input type="text" value={currentMessage} onChange={(e) => setCurrentMessage(e.target.value)} />
                <button onClick={sendMessage}>Send</button>
            </div>
        </div>     
        </> 
    )
}

export default Chat
